use indexmap::IndexMap;
use openapi::{Schema, SchemaData, SchemaKind};

/// This struct is used to generate the OpenAPI specification for a particular type. It is already
/// made available for all primitives and some other types from the rust standard library, and
/// you can also make your own types provide one through the [OpenapiType] trait and derive macro.
///
/// Note that this struct is marked non-exhaustive. This means that new attributes might be added
/// at any point in time without a breaking change. The only way to obtain a value is through the
/// [OpenapiSchema::new] method.
#[derive(Debug, Clone, PartialEq)]
#[non_exhaustive]
pub struct OpenapiSchema {
	/// The name of this schema. If it is None, the schema will be inlined.
	pub name: Option<String>,
	/// The description of this schema. Optional and only makes sense when a [`name`](Self::name)
	/// is set as well.
	pub description: Option<String>,
	/// Whether this particular schema is nullable. Note that there is no guarantee that this will
	/// make it into the final specification, it might just be interpreted as a hint to make it
	/// an optional parameter.
	pub nullable: bool,
	/// The actual OpenAPI schema.
	pub schema: SchemaKind,
	/// Other schemas that this schema depends on. They will be included in the final OpenAPI Spec
	/// along with this schema.
	pub dependencies: IndexMap<String, OpenapiSchema>
}

impl OpenapiSchema {
	/// Create a new schema that has no name.
	pub fn new(schema: SchemaKind) -> Self {
		Self {
			name: None,
			description: None,
			nullable: false,
			schema,
			dependencies: IndexMap::new()
		}
	}

	/// Convert this schema to a [Schema] that can be serialized to the OpenAPI Spec.
	pub fn into_schema(self) -> Schema {
		Schema {
			schema_data: SchemaData {
				nullable: self.nullable,
				title: self.name,
				description: self.description,
				..Default::default()
			},
			schema_kind: self.schema
		}
	}
}

/**
This trait needs to be implemented by every type that is being used in the OpenAPI Spec. It gives
access to the [OpenapiSchema] of this type. It is provided for primitive types, String and the
like. For use on your own types, there is a derive macro:

```
# #[macro_use] extern crate openapi_type_derive;
#
#[derive(OpenapiType)]
struct MyResponse {
	message: String
}
```
*/
pub trait OpenapiType {
	fn schema() -> OpenapiSchema;
}

impl<'a, T: ?Sized + OpenapiType> OpenapiType for &'a T {
	fn schema() -> OpenapiSchema {
		T::schema()
	}
}

use crate::OpenapiSchema;
use indexmap::IndexMap;
use openapiv3::{ReferenceOr, Schema, SchemaKind, Type};
use std::borrow::Cow;

pub type Dependencies = IndexMap<String, OpenapiSchema>;
pub type Properties = IndexMap<String, ReferenceOr<Box<Schema>>>;
pub type Required = Vec<String>;

fn add_dependencies(dependencies: &mut Dependencies, other: &mut Dependencies) {
	while let Some((dep_name, dep_schema)) = other.pop() {
		if !dependencies.contains_key(&dep_name) {
			dependencies.insert(dep_name, dep_schema);
		}
	}
}

pub fn inline_if_unnamed(
	dependencies: &mut Dependencies,
	mut schema: OpenapiSchema,
	doc: Option<&'static str>
) -> ReferenceOr<Schema> {
	add_dependencies(dependencies, &mut schema.dependencies);
	match schema.name.as_ref() {
		Some(schema_name) => {
			let ref_name = schema_name.replace(|c: char| !c.is_alphanumeric(), "_");
			let mut reference = "#/components/schemas/".to_string();
			reference.push_str(&ref_name);
			dependencies.insert(ref_name, schema);
			ReferenceOr::Reference { reference }
		},

		None => {
			let mut schema = schema.into_schema();
			if let Some(doc) = doc {
				schema.schema_data.description = Some(doc.to_string());
			}
			ReferenceOr::Item(schema)
		}
	}
}

struct FlattenError(Cow<'static, str>);

fn flatten_impl(properties: &mut Properties, required: &mut Required, schema: SchemaKind) -> Result<(), FlattenError> {
	let mut obj = match schema {
		SchemaKind::Type(Type::Object(obj)) => obj,
		SchemaKind::OneOf { .. } => {
			return Err(FlattenError(
				"#[serde(flatten)] is currently not supported for enums with non-unit variants".into()
			))
		},
		_ => return Err(FlattenError("Expected object".into()))
	};

	while let Some((prop_name, prop_schema)) = obj.properties.pop() {
		if properties.contains_key(&prop_name) {
			return Err(FlattenError(format!("Duplicate property name {}", prop_name).into()));
		}
		properties.insert(prop_name, prop_schema);
	}
	required.extend(obj.required.into_iter());

	Ok(())
}

pub fn flatten(
	dependencies: &mut Dependencies,
	properties: &mut Properties,
	required: &mut Required,
	mut schema: OpenapiSchema
) {
	add_dependencies(dependencies, &mut schema.dependencies);
	match flatten_impl(properties, required, schema.schema) {
		Ok(_) => {},
		Err(e) => panic!("Flattening produced an error: {}", e.0)
	};
}

