macro_rules! int {
	($($ty:ident($minimum:expr, $byte:expr);)+) => {
		$(
			impl OpenapiType for $ty {
				fn visit_type<V: Visitor>(visitor: &mut V) {
					visitor.visit_int($byte, $minimum);
				}
			}
		)+
	}
}

