	}

	fn visit_binary(&mut self) {
		self.panic_if_non_empty();
		*self = Self::Binary;
	}

	fn visit_option(&mut self) -> &mut Self {
		self.panic_if_non_empty();
		*self = Self::Option(Box::new(Self::new()));
